package test01;

public class tables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int j = 2; j <= 22; j++) {
			System.out.println("\nTable of " + j);

			for (int i = 1; i <= 10; i++) {
				System.out.println("  " + i + " X " + j + " = " + i * j);
			}

		}
	}

}
